#' @importFrom jmvcore Analysis
#' @importFrom R6 R6Class
MetaCorrBase <- R6::R6Class(
    "MetaCorrBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = 'MetaModel',
                name = 'MetaCorr',
                version = c(1,0,0),
                options = options,
                results = MetaCorrResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE)
        }))

#' Correlation Coefficients
#'
#' 
#' @param data .
#' @param rcor .
#' @param samplesize .
#' @param slab .
#' @param moderatorcor .
#' @param includemods .
#' @param methodmetacor .
#' @param cormeasure .
#' @param level .
#' @param addcred .
#' @param addfit .
#' @param showweights .
#' @param xAxisTitle .
#' @param forestOrder .
#' @param fsntype .
#' @param yaxis .
#' @param yaxisInv .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$textRICH} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tableTauSqaured} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tableQTest} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$pubBias$fsn} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$pubBias$rankRICH} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$pubBias$regRICH} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$funplot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$textRICH$asDF}
#'
#' \code{as.data.frame(results$textRICH)}
#'
#' @export
MetaCorr <- function(
    data,
    rcor,
    samplesize,
    slab,
    moderatorcor,
    includemods = FALSE,
    methodmetacor = "REML",
    cormeasure = "ZCOR",
    level = 95,
    addcred = FALSE,
    addfit = TRUE,
    showweights = FALSE,
    xAxisTitle,
    forestOrder = "fit",
    fsntype = "Rosenthal",
    yaxis = "sei",
    yaxisInv = FALSE) {

    options <- MetaCorrOptions$new(
        rcor = rcor,
        samplesize = samplesize,
        slab = slab,
        moderatorcor = moderatorcor,
        includemods = includemods,
        methodmetacor = methodmetacor,
        cormeasure = cormeasure,
        level = level,
        addcred = addcred,
        addfit = addfit,
        showweights = showweights,
        xAxisTitle = xAxisTitle,
        forestOrder = forestOrder,
        fsntype = fsntype,
        yaxis = yaxis,
        yaxisInv = yaxisInv)

    results <- MetaCorrResults$new(
        options = options)

    analysis <- MetaCorrClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
