#' }
#'
#' @export
metamdms <- function(
    data,
    n1i,
    m1i,
    sd1i,
    n2i,
    m2i,
    sd2i,
    slab,
    moderator,
    includemods = FALSE,
    methodmetamdms = "REML",
    mdmsmeasure = "SMD",
    level = 95,
    addcred = FALSE,
    addfit = TRUE,
    showweights = FALSE,
    fsntype = "Rosenthal",
    yaxis = "sei") {

    options <- metamdmsOptions$new(
        n1i = n1i,
        m1i = m1i,
        sd1i = sd1i,
        n2i = n2i,
        m2i = m2i,
        sd2i = sd2i,
